// class representing a warntext
class WarnText
{
  // constructor taking title, warntext and comment
  constructor(title, text, comment)
  {
    this.title = title;
    this.text = text;
    this.comment = comment;
    this.id = WarnText.uuidv4();
  }

  // static function to create an unique identifier for warntexts
  static uuidv4() {
    return "10000000-1000-4000-8000-100000000000".replace(/[018]/g, c =>
       (+c ^ crypto.getRandomValues(new Uint8Array(1))[0] & 15 >> +c / 4).toString(16)
    );
  }
}

// collection class for warntexts
class WarnTextCollection
{
  // constructor taking warntexts of categories common, provilecontent warns and profile picture warns
  constructor(commonWarns, profileContentWarns, profilePictureWarns, verifyWarns)
  {
    this.commonWarns = commonWarns;
    this.profileContentWarns = profileContentWarns;
    this.profilePictureWarns = profilePictureWarns;
    this.verifyWarns = verifyWarns;
  }

  // function to save the warntexts to local storage
  save()
  {
     localStorage.setItem('warnTexts', JSON.stringify(this));
  }

  // function to load the warntexts from localstorage
  static load()
  {
    // get data from localstorage and parse to json
    var result = JSON.parse(localStorage.getItem('warnTexts'));

    // if there is an result create warntext collection and return it
    if(result != null)
      return new WarnTextCollection(result.commonWarns ?? [], result.profileContentWarns ?? [], result.profilePictureWarns ?? [], result.verifyWarns ?? []);
    // otherwise return empty collection
    else
      return new WarnTextCollection([], [], [], []);
  }
}

