// class for holding base variables
class BaseVariables
{
  // constructor
  constructor()
  {
    // initialize and load settings
    this.settings = new Settings();
    this.settings.load();

    // get report id from report details
    this.reportID = $('h1:contains("Knuddels.de - Meldesystem")').text().replace('Knuddels.de - Meldesystem - Meldung ', '').split(' ')[0];
    // get reported user from report details
    this.reportedUser = $($("h3 div span").filter(function () { return $(this).css('color') === 'rgb(153, 0, 0)'; }).get(0)).text();
    // url to changelog of the script
    this.changeLogUri = 'https://raw.githubusercontent.com/inflames2k/Scripts/refs/heads/main/changelog.html';
    // get report type from report details
    this.reportType = $("h3:contains('Typ:')").children().last().text().replace($("h3:contains('Typ:')").children().last().children().first().text(), '');

    // get the current logged in user
    this.currentUser = $('div:contains("Du bist eingeloggt als:") > b').text().trim();
    // get the user that has processed the report at last
    this.processedUser = $('div:contains("Aktuell zugewiesen")').last().next().text().trim();

    this.BaseUri = 'https://' + window.location.host + ':' + window.location.port;


    var textElement = $("div:contains('Wann/Wo:')").last().next();
    var fullText = textElement.text();
    var dateMatch = fullText.match(/(\d{2}\.\d{2}\.\d{4})/);
    this.ReportDate = dateMatch ? dateMatch[0] : null;
  }
}


