     var baseVariables = new BaseVariables();

     // bootstrap reporting system changes
     function bootStrap()
     {
       $.ajaxSetup ({
            // Disable caching of AJAX responses
            cache: false
        });

        // add report details to overview (reporter, reported user and Channels)
        addReportNames();

        // activate autorefresh ov overview
        autoRefresh();
        // add report overlay for overviews
        addOverlay();
        // add config view
        addConfigshow();
        // modify navigation
        modifyNavigation();
        // modify main page layout
        modifyLayout();

        // append script name and script version to copyright footer
        $('#footer').html($('#footer').html() + " - " + GM_info.script.name + " " + GM_info.script.version);



        // modify log (report content)
        modifyLog();

                      // set settings to settings dialog
        applySettings();


        // set report quota to report statistics
        setReportQuota();


        // check if there are remind reports for today or for previous days
        var openReminders = baseVariables.settings.remindReports?.filter(remindReport => {
          const today = new Date();
          today.setHours(0, 0, 0, 0);

          return new Date(remindReport.remindDate) <= today;
        });

        if(openReminders?.length == 1)
          showModernToast("<br>Es liegt eine Meldung zur Wiedervorlage vor!<br><br><center><a href='" + baseVariables.BaseUri + "/ac/ac_overview.pl?domain=knuddels.de&state=3'>Zu den Wiedervorlagen</a></center>", 5000);
        else if(openReminders?.length > 1)
          showModernToast("<br>Es liegen " + openReminders.length + " Meldungen zur Wiedervorlage vor!<br><br><center><a href='" + baseVariables.BaseUri + "/ac/ac_overview.pl?domain=knuddels.de&state=3'>Zu den Wiedervorlagen</a></center>", 5000);
     }

     // function to show a toast message
     function showModernToast(message, duration) {
          duration = duration || 5000; // Standard: 5 Sekunden

          // Container erstellen, falls nicht vorhanden
          if ($("#toast-container").length === 0) {
            $("<div>").attr("id", "toast-container").appendTo("body");
          }

          // Toast-Element erstellen
          var toastId = "toast-" + Date.now();
          var $toast = $("<div>")
            .addClass("modern-toast")
            .attr("id", toastId)
            .html(message) // .html() statt .text() um HTML-Inhalte zu untersttzen
            .appendTo("#toast-container");

          // Animation starten
          setTimeout(function() {
            $toast.addClass("visible");
          }, 100);

          // Nach definierter Zeit ausblenden
          setTimeout(function() {
            $toast.addClass("hiding");

            // Nach Ende der Animation entfernen
            setTimeout(function() {
              $toast.remove();
            }, 400);
          }, duration);

          return $toast;
    }

