    function importSettings(uri)
    {
      fetch(uri)
       .then(res => res.json())
       .then(out => {
             var settings = baseVariables.settings;
             settings.enableReportOverlay = out.enableReportOverlay;
             settings.enableReportRequestlink = out.enableReportRequestlink;
             settings.currentStyle = out.currentStyle;
             settings.enableAutoRefresh = out.enableAutoRefresh;
             settings.overViewRefreshInterval = out.overViewRefreshInterval;
             settings.expandReportContents = out.expandReportContents;
             settings.enableActionFilter = out.enableActionFilter;
             settings.enableGuiltyFilter = out.enableGuiltyFilter;
             settings.colorSelectReporter = out.colorSelectReporter;
             settings.colorSelectReported = out.colorSelectReported;
             settings.enableDeeplTranslation = out.enableDeeplTranslation;
             settings.enableDeeplTranslationRow = out.enableDeeplTranslationRow;
             settings.deeplAPIKey = out.deeplAPIKey;
             settings.showDetailsInOverview = out.showDetailsInOverview;
             settings.warnTextCollection = new WarnTextCollection(out.warnTextCollection.commonWarns, out.warnTextCollection.profileContentWarns, out.warnTextCollection.profilePictureWarns);
             settings.save();

             bindWarns();
             applySettings();

             showModernToast("Einstellungen wurden erfolgreich importiert.");
        })
        .catch(err => console.log(err));
    }

    function importWarns(uri, doAlert = true)
    {
       fetch(uri)
       .then(res => res.json())
       .then(out => {
             baseVariables.settings.warnTextCollection = new WarnTextCollection(out.commonWarns, out.profileContentWarns, out.profilePictureWarns);
             baseVariables.settings.warnTextCollection.save();

             bindWarns();
             if(doAlert)
              showModernToast("Verwarntexte wurden erfolgreich importiert.");
        })
        .catch(err => console.log(err));
    }

    // filters history reports for guilty reports
    function filterGuilty()
    {
      if(/ac_viewcase.pl/.test(window.location.href) || /ac_editcase.pl/.test(window.location.href))
      {
        if((!$('input[onclick="toggleVisibilityOfUnguiltyCases(this)"]').is(':checked') && baseVariables.settings.enableGuiltyFilter =="ja")
          || $('input[onclick="toggleVisibilityOfUnguiltyCases(this)"]').is(':checked'))
          $('input[onclick="toggleVisibilityOfUnguiltyCases(this)"]')?.click();


        var elevateHint = $('p:contains("Aufgefhrt als"), p:contains("HINWEIS: Der Kommentar")');

        elevateHint.each(function() {
          $(this).parent().prev('br').remove();
          $(this).parent().prev('br').remove();

          $(this).remove();
        });

        $('input[type="submit"]').parent().after('<br>');
      }
    }

