    // set a given warning editable
    function setEditable(warning)
    {
      var index = 0;
      $('#' + warning.id).siblings().each(function() {

        var text = $(this).text();
        if(index >= 0 && index < 3){
          $(this).empty().append('<textarea style="width: 100%; height: 100px;">' + text + '</textarea>');}
        else if(index == 3)
          $(this).empty().append('<a href="#" class="confirmEdit' + warning.id + '"></a>');
        else if(index == 4)
          $(this).empty();
        index++;
      });

      $('.confirmEdit' + warning.id).data('warn', warning);

      $('.confirmEdit' + warning.id).on("click", function() {
         // confirm edit... lets see how and ... bla
        var warn = $(this).data('warn');
        var cur = 0;
        $(this).parent().parent().children().each(function(){
          if(cur == 1)
            warn.title = $(this).children().val();
          else if(cur == 2)
            warn.text = $(this).children().val();
          else if(cur == 3)
            warn.comment = $(this).children().val();

          cur++;
        });

        bindCommonWarns();
        bindProfilePictureWarns();
        bindProfileContentWarns();
        bindVerifyWarns();

        baseVariables.settings.warnTextCollection.save();
      });
    }

    // function to bind warntexts to config view
    function bindWarnsToView(parentDiv, warnTexts)
    {
      $('#' + parentDiv + ' tr:not(:first-child)').remove();
      var i = 0;
      for(i = 0; i < warnTexts.length; i++)
      {
          $('#' + parentDiv).append(
            `
              <tr>
                <td style="width: 0px; display: none;" id="` + warnTexts[i].id + `">
                <td>` + warnTexts[i].title + `</td>
                <td style="padding-left: 5px; white-space: pre-wrap;">` + warnTexts[i].text + `</td>
                <td style="padding-left: 5px; white-space: pre-wrap;">` + warnTexts[i].comment + `</td>
                <td><center><a href="#" class="edit` + warnTexts[i].id + `" title="Bearbeiten/Editieren"></a></center></td>
                <td><center><a href="#" class="remove` + warnTexts[i].id + `" title="Lschen"></a></center></td>
              </tr>`
          );

          // set warning as data for the row
          $('.edit' + warnTexts[i].id).data('warn', warnTexts[i]);
          $('.remove' + warnTexts[i].id).data('warn', warnTexts[i]);

          // function on edit button click
          $('.edit' + warnTexts[i].id).on("click", function() {
            // set row to edit mode
            setEditable($(this).data('warn'));
          });

          // function on remove button click
          $('.remove' + warnTexts[i].id).on("click", function() {
            // remove the warning
            removeWarning(warnTexts, $(this).data('warn'));
          });
      }
    }

    // function to bind common warnings to settings view
    function bindCommonWarns()
    {
      bindWarnsToView('commonWarnDiv', baseVariables.settings.warnTextCollection.commonWarns);
    }

