    // function to bind warns
    function bindWarns()
    {
      bindCommonWarns();
      bindProfilePictureWarns();
      bindProfileContentWarns();
      bindVerifyWarns();
    }

    // function to remove a given warning from a given collection
    function removeWarning(collection, warning)
    {
      // remove the warning from the collection
      collection.splice(collection.indexOf(warning), 1);
      // refresh view table
      bindWarns();

      // save changed settings
      baseVariables.settings.warnTextCollection.save();
    }

