    function highlightText(text, logIndex) {
        // Get the log container
        const logContainer = document.getElementById('log' + logIndex);
        if (!logContainer) {
            console.error('Log container not found for index:', logIndex);
            return;
        }

        // WICHTIG: Immer erst alle vorhandenen Hervorhebungen entfernen
        removeHighlights(logContainer);

        // If text is empty, we're done (highlights already removed)
        if (!text || text.trim() === '') {
            return;
        }

        // Get all content-type-section divs within this log
        const contentSections = logContainer.querySelectorAll('.content-type-section');

        // Escape special regex characters in the search text
        const escapedText = text.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

        // Create regex for case-insensitive matching
        const regex = new RegExp(escapedText, 'gi');

        // Process each content section
        contentSections.forEach(section => {
            highlightInNode(section, regex);
        });
    }

    function removeHighlights(container) {
        // Find all highlighted spans
        const highlightedSpans = container.querySelectorAll('.highlighted-text');

        highlightedSpans.forEach(span => {
            // Replace the span with its text content
            const textNode = document.createTextNode(span.textContent);
            span.parentNode.replaceChild(textNode, span);
        });

        // Normalize text nodes (merge adjacent text nodes)
        container.normalize();
    }

    function highlightInNode(node, regex) {
        // Skip if already highlighted
        if (node.classList && node.classList.contains('highlighted-text')) {
            return;
        }

        // Process text nodes
        if (node.nodeType === Node.TEXT_NODE) {
            const text = node.textContent;
            if (regex.test(text)) {
                // Create a span with highlighting
                const fragment = document.createDocumentFragment();
                let lastIndex = 0;

                // Reset regex lastIndex
                regex.lastIndex = 0;
                let match;

                while ((match = regex.exec(text)) !== null) {
                    // Add text before match
                    if (match.index > lastIndex) {
                        fragment.appendChild(
                            document.createTextNode(text.substring(lastIndex, match.index))
                        );
                    }

                    // Add highlighted match
                    const span = document.createElement('span');
                    span.className = 'highlighted-text';
                    span.style.backgroundColor = '#ffff00';
                    span.style.fontWeight = 'bold';
                    span.style.color = '#000';
                    span.textContent = match[0];
                    fragment.appendChild(span);

                    lastIndex = match.index + match[0].length;
                }

                // Add remaining text
                if (lastIndex < text.length) {
                    fragment.appendChild(
                        document.createTextNode(text.substring(lastIndex))
                    );
                }

                // Replace the text node with the fragment
                node.parentNode.replaceChild(fragment, node);
            }
        } else if (node.nodeType === Node.ELEMENT_NODE) {
            // Recursively process child nodes
            // Create array copy since we might modify the DOM
            const children = Array.from(node.childNodes);
            children.forEach(child => highlightInNode(child, regex));
        }
    }

