    // function to set report quota to statistics
    function setReportQuota()
    {
        // only do if page is statistic
        if(/ac_judgestatistic.pl/.test(window.location.href))
        {
            // get all statistic tables
            var $tables = $('table:contains("nicht bearbeitet")');
            // get first statistic table
            var $primaryTable = $tables.first();

            $primaryTable.find('th:first').before('<th>Nickname</th>');
            $primaryTable.find('th:last').attr('colspan',3);

            var assignedReportsSum = 0;
            var processedReportsSum = 0;
            var quotaSum = 0;
            var quotaCount = 0;


            $tables.each(function(index) {
                var assignedReports = parseFloat($(this).find('td:first-child').text().replace('.', ''));
                var processedReports = parseFloat($(this).find('span[style="color:#0A0"]:last-child').text().replace('.', ''));
                var notProcessedReports = parseFloat($(this).find('span[style="color:f00"]').text().replace('.', ''));
                quotaCount ++;

                var nickName = $(this).prev('h3').text();
                $(this).prev('h3').remove();
                $(this).find('tr').css('font-size', '15px');

                $(this).find('td:first-child').next().next().html('<span style="color: #FE9A2E"><b>' + $(this).find('td:first-child').next().next().html() + '</b></span>');
                $(this).find('span[style="color:#0A0"]:last-child').parent().css('width', '115px');

                // add nick name color
                $(this).find('td:first-child').before('<td><B><a class="blind" target="_blank" href="'+ baseVariables.BaseUri + '/ac/ac_search.pl?domain=knuddels.de&mode=search&involvednick=' + nickName + '&involvementtype=0">' + nickName + '</a></b></td>');

                assignedReportsSum += assignedReports;
                processedReportsSum += processedReports;
                if(assignedReports == 0)
                 assignedReports = 1;

                var quota = Math.round(processedReports * 100);
                quota = Math.round(parseFloat(quota / assignedReports));

                if(processedReports == 0)
                  quota = 0;

                quotaSum += quota;


                var quotaColor = getQuotaColor(quota, assignedReports);
                $(this).find('td:last').before('<td style="text-align: right;"><span style="color: #0A0"><b>' + $(this).find('span[style="color:#0A0"]:last-child').text() + '</b></span></td>');
                $(this).find('td:last').before('<td style="text-align: right;"><span style="color: ' + quotaColor + '">[<b>' + quota.toLocaleString() + '%</b>]</span></td>');

                $(this).find('span[style="color:#0A0"]:last-child').remove();

                if(index != 0)
                {
                  //alert($(this).find('tr:last').html());
                  $primaryTable.find('tr:last').after($(this).find('tr:last'));

                  $(this).remove();
                }
            });

          if(quotaCount > 1)
          {
            var quota = Math.round(processedReportsSum * 100);
            quota = Math.round(parseFloat(quota / assignedReportsSum));

           // var quota = Math.round(quotaSum / quotaCount);
            var quotaColor = getQuotaColor(quota, processedReportsSum);

            $primaryTable.find('tr:last').after('<tr><td colspan="7" style="border-bottom: 3px solid rgb(82, 65, 110)"></tr>');
            $primaryTable.find('tr:last').after(`
              <tr>
                <td><b>Summe</b></td>
                <td style="text-align: right"><b>` + assignedReportsSum + `</b></td>
                <td />
                <td />
                <td style="text-align: right"><b><span style="color: #0A0">` + processedReportsSum.toLocaleString() + `</span></b></td>
                <td style="text-align: right"><span style="color: ` + quotaColor + `">[<b>` + quota.toLocaleString() + `%</b>]</span></td>
                <td />
                <td />
              </tr>
            `);
          }
        }
    }

    function getQuotaColor(quota, assignedReports)
    {
        if(quota < 85 && assignedReports > 0)
          return '#f00';
        else if(quota < 100 && assignedReports > 0)
          return '#74DF00'
        else
          return '#0A0'
    }

