    function getChannelHistory(remoteObject, sectionName) {
      const headers = remoteObject.find('h3');
      let section = null;

      headers.each(function() {
        if ($(this).text().includes(sectionName)) {
          section = $(this);
        }
      });

      if (!section) return '';

      let current = section.next();
      while (current.length) {
        if (current.text().includes('Channel-History:')) {
          const contentDiv = current.next();
          const html = contentDiv.html() || '';
          const lines = html.split('<br>');
          const entries = [];

          lines.forEach(line => {
            const text = line.replace(/<[^>]+>/g, '').trim();
            if (text.match(/^\d{2}\.\d{2}\.\d{4}/)) {
              const channelMatch = text.match(/\| Channel:\s*(.+)/);
              if (channelMatch && channelMatch[1].trim()) {
                const channelName = channelMatch[1].trim()
                  .replace('[E]', '<span style="color:#BB1111;font-weight:bold">[E]</span>');
                entries.push(channelName);
              }
            }
          });

          if (entries.length <= 3) {
            return entries.join(', ');
          } else {
            const visible = entries.slice(0, 3).join(', ');
            const remaining = entries.length - 3;
            return `${visible} <span style="color: #666; font-size: 0.9em;">(+${remaining} weitere)</span>`;
          }
        }
        current = current.next();
      }

      return '';
    }

