// class representing a warntext
class WarnText
{
  // constructor taking title, warntext and comment
  constructor(title, text, comment)
  {
    this.title = title;
    this.text = text;
    this.comment = comment;
    this.id = WarnText.uuidv4();
  }

  // static function to create an unique identifier for warntexts
  static uuidv4() {
    return "10000000-1000-4000-8000-100000000000".replace(/[018]/g, c =>
       (+c ^ crypto.getRandomValues(new Uint8Array(1))[0] & 15 >> +c / 4).toString(16)
    );
  }
}

// collection class for warntexts
class WarnTextCollection
{
  // constructor taking warntexts of categories common, provilecontent warns and profile picture warns
  constructor(commonWarns, profileContentWarns, profilePictureWarns)
  {
    this.commonWarns = commonWarns;
    this.profileContentWarns = profileContentWarns;
    this.profilePictureWarns = profilePictureWarns;
  }

  // function to save the warntexts to local storage
  save()
  {
     localStorage.setItem('warnTexts', JSON.stringify(this));
  }

  // function to load the warntexts from localstorage
  static load()
  {
    // get data from localstorage and parse to json
    var result = JSON.parse(localStorage.getItem('warnTexts'));

    // if there is an result create warntext collection and return it
    if(result != null)
      return new WarnTextCollection(result.commonWarns ?? [], result.profileContentWarns ?? [], result.profilePictureWarns ?? []);
    // otherwise return empty collection
    else
      return new WarnTextCollection([], [], []);
  }
}

// class representing user settings
class Settings
{
   constructor()
   {
   }

   // function to load user settings
   load()
   {
     // setting if report overlay on report overviews should be active
     this.enableReportOverlay = localStorage.getItem("enableReportOverlay") ?? "ja";
     // setting if "Meldung beantragen" should be shown in main menu
     this.enableReportRequestlink = localStorage.getItem("newReportLink") ?? "aus";
     // setting of the current style (light, Dark)
     this.currentStyle = localStorage.getItem("reportStyle") ?? "Light";
     // setting if autorefresh is active
     this.enableAutoRefresh = localStorage.getItem("enableAutoRefresh") ?? "nein";
     // interval for refreshing overview
     this.overViewRefreshInterval = localStorage.getItem("refreshInterval") ?? 5000;
     // setting if report contents should be expanded by default
     this.expandReportContents = localStorage.getItem("expandReportContents") ?? "nein";
     // setting if report actions should be filtered by report type
     this.enableActionFilter = localStorage.getItem("enableActionFilter") ?? "ja";
     // setting if filter "Meldungen ausblenden, in denen die/der Gemeldete nicht schuldig war" should be active by default
     this.enableGuiltyFilter = localStorage.getItem("enableGuiltyFilter") ?? "nein";
     // color for reporting user
     this.colorSelectReporter = localStorage.getItem("colorSelectReporter") ?? "#01DF01";
     // color for reported user
     this.colorSelectReported = localStorage.getItem("colorSelectReported") ?? "#FE2E2E";
     // setting if deepl translation api should be active
     this.enableDeeplTranslation = localStorage.getItem("enableDeeplTranslation") ?? "nein";
     // setting if translation button on each message should be shown by default
     this.enableDeeplTranslationRow = localStorage.getItem("enableDeeplTranslationRow") ?? "nein";
     // api key for deepl translator
     this.deeplAPIKey = localStorage.getItem("deeplAPIKey");
     // warntext collection
     this.warnTextCollection = WarnTextCollection.load();
   }

   // function to save the user settings
   save()
   {
     localStorage.setItem("newReportLink", this.enableReportRequestlink);
     localStorage.setItem("reportStyle", this.currentStyle);
     localStorage.setItem("refreshInterval", this.overViewRefreshInterval);
     localStorage.setItem("expandReportContents", this.expandReportContents);
     localStorage.setItem("enableReportOverlay", this.enableReportOverlay);
     localStorage.setItem("enableAutoRefresh", this.enableAutoRefresh);
     localStorage.setItem("enableActionFilter", this.enableActionFilter);
     localStorage.setItem("enableGuiltyFilter", this.enableGuiltyFilter);
     localStorage.setItem("colorSelectReporter", this.colorSelectReporter);
     localStorage.setItem("colorSelectReported", this.colorSelectReported);
     localStorage.setItem("enableDeeplTranslation", this.enableDeeplTranslation);
     localStorage.setItem("enableDeeplTranslationRow", this.enableDeeplTranslationRow);
     localStorage.setItem("deeplAPIKey", this.deeplAPIKey);
     this.warnTextCollection.save();
   }
}

// class for holding base variables
class BaseVariables
{
  // constructor
  constructor()
  {
    // initialize and load settings
    this.settings = new Settings();
    this.settings.load();

    // get report id from report details
    this.reportID = $('h1:contains("Knuddels.de - Meldesystem")').text().replace('Knuddels.de - Meldesystem - Meldung ', '').split(' ')[0];
    // get reported user from report details
    this.reportedUser = $($("h3 div span").filter(function () { return $(this).css('color') === 'rgb(153, 0, 0)'; }).get(0)).text();
    // url to changelog of the script
    this.changeLogUri = 'https://raw.githubusercontent.com/inflames2k/Scripts/refs/heads/main/changelog.html';
    // get report type from report details
    this.reportType = $("h3:contains('Typ:')").children().last().text().replace($("h3:contains('Typ:')").children().last().children().first().text(), '');

    // get the current logged in user
    this.currentUser = $('div:contains("Du bist eingeloggt als:") > b').text().trim();
    // get the user that has processed the report at last
    this.processedUser = $('div:contains("Aktuell zugewiesen")').last().next().text().trim();
  }
}


