    // function to translate a message
    function translateMessage(messageContainer)
    {
      // only do if deepl api key is set
      if(baseVariables.settings.deeplAPIKey)
      {
        // if message is not translated allready do translaction
        if(!messageContainer.hasClass("translateActive"))
        {
          // set translated
          messageContainer.addClass("translateActive");

          // get text content of the message
          var text = messageContainer.parent().clone();
          text.find('span:first, span:last,style').remove();
          text.find('.translate').remove();

          // request the translation using the deppl free api
          GM_xmlhttpRequest({
                method: 'GET',
                url: 'https://api-free.deepl.com/v2/translate?auth_key=' + baseVariables.settings.deeplAPIKey + '&text=' + text.text() + '&target_lang=DE',
                onload: function(res) {
                  // if status returned is 403 the api key is invalid
                  // show to the user!
                  if(res.status == "403")
                    alert("Keine Berechtigungen. API Key prfen!");
                  // else parse response
                  else
                  {
                    var result = JSON.parse(res.responseText);

                    // if there has been found a translaction and the detected source language is not german
                    // append the translated text to the message
                    if(result.translations?.length > 0)
                    {
                      if(result.translations[0].detected_source_language != "DE")
                      {
                        messageContainer.parent().find('span:first').after(result.translations[0].text +  " [");
                        messageContainer.parent().children().last().before(' (' +  result.translations[0].detected_source_language  + ')</u>]');
                      }
                    }
                    // no translation found
                    else
                      messageContainer.parent().html(messageContainer.parent().html() + ' [KEINE BERSETZUN GEFUNDEN]');
                  }
                }
            });
        }
      }
      // no deepl api key set, show error message
      else
        alert("Es muss ein API Key fr die Free API von deepl.com zur Verwendung der bersetzung konfiguriert werden.");
    }

    // function to translate a full log by given log id
    function translateLog(id)
    {
      // iterate through each message and translate them
      $("#log" + id + " .translate").each(function() {
        translateMessage($(this));
      });
    }

