    // function to set report quota to statistics
    function setReportQuota()
    {
        // only do if page is statistic
        if(/ac_judgestatistic.pl/.test(window.location.href))
        {
            $('table:contains("nicht bearbeitet")').each(function() {
                var assignedReports = parseFloat($(this).find('td:first-child').text().replace('.', ''));
                var processedReports = parseFloat($(this).find('span[style="color:#0A0"]:last-child').text().replace('.', ''));

                $(this).find('td:first-child').next().next().html('<span style="color: #FE9A2E"><b>' + $(this).find('td:first-child').next().next().html() + '</b></span>');
                $(this).find('span[style="color:#0A0"]:last-child').parent().css('width', '115px');

                if(assignedReports == 0)
                 assignedReports = 1;

                var quota = Math.round(processedReports * 100);
                quota = Math.round(parseFloat(quota / assignedReports));

                if(processedReports == 0)
                  quota = 0;

                var color = '';
                if(quota < 85 && assignedReports > 0)
                    color = '#f00';
                else if(quota < 100 && assignedReports > 0)
                    color = '#74DF00'
                else
                    color = '#0A0'

                var quotaText = ' - <span style="color: ' + color + '">[<b>' + quota + '%</b>]</span>';
                $(this).find('span[style="color:#0A0"]:last-child').html('<b>' + $(this).find('span[style="color:#0A0"]:last-child').text() + '</b>' + quotaText);
            });
        }
    }

