    // function to add reporter, reported user and channel to overview
    function addReportNames()
    {
       // only do if page is overview
       if (/ac_overview.pl/.test(window.location.href)) {

        $('table tbody tr').each(function(index) {
            const row = $(this);
            const reportLink = row.find('a.blind').attr('href');
            if (reportLink)
            {
                row.append('<td class="Q reportInfo' + index + '"></td>');
                GM_xmlhttpRequest({
                    method: 'GET',
                    url: reportLink,
                    onload: function(res) {
                        const headerRow = $('table tr').first();
                        if (headerRow.length && !headerRow.find('th:contains("Accountnamen")').length) {
                            headerRow.append('<th class="Q">Accountnamen<br>Channel</th>');
                        }

                        var remoteObject = $($.parseHTML(res.responseText));
                        var reported = remoteObject.find('div[style*="width:140px"]:contains("Gemeldete(r)"):first()').next().text();
                        reported = reported.substring(0, reported.indexOf('('));
                        var reporter = remoteObject.find('div[style*="width:140px"]:contains("Meldende(r)"):first()').next().text();
                        reporter = reporter.substring(0, reporter.indexOf('('));
                        var channel = remoteObject.find('span[title*="Channel"]:first()').text();

                        $('.reportInfo' + index).html('<span class="userReporting"><b>' + (reporter || 'Unbekannt') + '</b></span><br><span class="userReported"><b>' + (reported || 'Unbekannt') + '</b></span><br><b>Channel: </b>' +  channel);

                      setReportingColor();
                    }
                });
            }
        });
      }
    }

