    // function to link all report ids in report
    function setReportLinks()
    {
      if(/ac_viewcase.pl/.test(window.location.href) || /ac_editcase.pl/.test(window.location.href))
      {
        let regex = XRegExp('[\*]{0,1}\\d{10}|\\d.\\d{3}.\\d{3}.\\d{3}', 'gms');
        var m = null;
        var content = null;
        var current = null;
        $('b:contains("E-Mail")').each(function() {

           $(this).parent().css('max-width', '500px');

           $(this).parent().children('div').each(function() {
             // add line breaks to admin info entry
             $(this).html($(this).html() + "<br><br>");
             // set max with of admin info entry
             $(this).css("max-width", "850px");

             content = $(this).html();

             // replace report id's with links to report
             while((m = regex.exec(content)) !== null)
             {
               $(this).html($(this).html().replaceAll(m[0], '<a target="_blank" href="https://www6.knuddels.de:8443/ac/ac_viewcase.pl?domain=knuddels.de&id=' + m[0].replaceAll("*", "").replaceAll('.', "") + '">' + m[0] + '</a>'));
             }
           });
        });

        // replace report id's with links to report in comments
        $('.commentOutput,.caseinfo2').each(function() {
          content = $(this).html();
          while((m = regex.exec(content)) !== null)
          {
            $(this).html($(this).html().replaceAll(m[0], '<a target="_blank" href="https://www6.knuddels.de:8443/ac/ac_viewcase.pl?domain=knuddels.de&id=' + m[0].replaceAll("*", "").replaceAll('.', "") + '">' + m[0] + '</a>'));
          }
        });

        try
        {
          if($('#header:contains("geschlossen")').text().trim() == ""
             || baseVariables.processedUser.trim() != baseVariables.currentUser.trim())
            showInputs();

          var buttons = $('button[onClick="history.go(0);"]');
          buttons.each(function() {
            $(this).attr('onClick', null);
          });
          buttons.on("click", function() {
            showLinks();
          });

        /*  var buttons = $('input[type="submit"]').on("click", function() {
            $('textarea[name="comment"]').val($('textarea[name="comment"]').val().replaceAll('_', '\\_').replaceAll('"', '\\"'));
            return false;
          }); */
        }
        catch {}
      }

      $('.sanction-option').parent().addClass("sanctionSelect");
    }

